import React, { useState, useEffect } from "react";

const SECTIONS = {
  education: /education|degree|university|college/i,
  skills: /skills|competencies/i,
  experience: /experience|employment|career/i,
  projects: /projects?/i,
  certifications: /certifications?|licensed?/i,
  contact: /contact|email|phone|linkedin|github|address/i
};

const TEMPLATES = {
  modern: { name: "Modern", styles: { font: "Georgia", header: "#2563eb", text: "#1f2937" } },
  minimal: { name: "Minimal", styles: { font: "Arial", header: "#374151", text: "#111827" } },
  corporate: { name: "Corporate", styles: { font: "Times New Roman", header: "#1f2937", text: "#111827" } },
  creative: { name: "Creative", styles: { font: "Helvetica", header: "#7c3aed", text: "#1f2937" } }
};

const BOT = {
  greeting: "Hello! I'm your Resume Assistant. How can I help?",
  ats: "ATS looks for keywords & standard headings. Use terms like 'Experience', 'Skills', 'Education'.",
  keywords: "Use action verbs (managed, developed) and quantify achievements.",
  format: "Keep formatting clean: consistent fonts, headings, bullet points.",
  sections: "Essential: Contact, Summary, Work, Skills, Education. Optional: Projects, Certifications.",
  default: "I can help with ATS, keywords, formatting, sections. What do you want to improve?"
};

export default function App() {
  const [text, setText] = useState(""), [analysis, setAnalysis] = useState(null);
  const [template, setTemplate] = useState("modern");
  const [chatOpen, setChatOpen] = useState(false);
  const [chat, setChat] = useState([{ type: "bot", msg: BOT.greeting }]);
  const [input, setInput] = useState("");

  useEffect(() => {
    if (!text.trim()) return;
    const found = {}, missing = [];
    Object.entries(SECTIONS).forEach(([k, r]) => (r.test(text) ? (found[k] = 1) : missing.push(k)));
    setAnalysis({ found, missing, score: Math.max(0, 100 - missing.length * 15) });
  }, [text]);

  const handleFile = f => {
    if (f?.type === "text/plain") {
      const r = new FileReader();
      r.onload = e => setText(e.target.result);
      r.readAsText(f);
    }
  };

  const download = () => {
    if (!text) return;
    const t = TEMPLATES[template];
    const blob = new Blob([
      `<html><head><style>body{font-family:${t.styles.font};color:${t.styles.text};padding:30px} .h{background:${t.styles.header};color:#fff;padding:20px;border-radius:8px;text-align:center;margin-bottom:20px}</style></head><body><div class='h'><h1>Professional Resume</h1></div><pre>${text}</pre></body></html>`
    ], { type: "text/html" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "resume.html";
    a.click();
  };

  const send = () => {
    if (!input.trim()) return;
    const u = input.toLowerCase();
    const res = u.includes("ats") ? BOT.ats : u.includes("keyword") ? BOT.keywords : u.includes("format") ? BOT.format : u.includes("section") ? BOT.sections : u.includes("hi") ? BOT.greeting : BOT.default;
    setChat([...chat, { type: "user", msg: input }, { type: "bot", msg: res }]);
    setInput("");
  };

  return (
    <div style={{ fontFamily: "sans-serif", padding: 20 }}>
      <h1>ðŸš€ Resume Analyzer</h1>
      <input type="file" accept=".txt" onChange={e => handleFile(e.target.files[0])} />
      {text && (
        <>
          <h2>ðŸŽ¨ Template</h2>
          {Object.keys(TEMPLATES).map(k => (
            <button key={k} onClick={() => setTemplate(k)} style={{ margin: 5, background: template === k ? "#2563eb" : "#eee", color: template === k ? "#fff" : "#000" }}>{TEMPLATES[k].name}</button>
          ))}
          <h2>ðŸ“Š Analysis</h2>
          <p>ATS Score: {analysis.score}%</p>
          <p>Found: {Object.keys(analysis.found).join(", ") || "None"}</p>
          <p>Missing: {analysis.missing.join(", ") || "None"}</p>
          <button onClick={download}>ðŸ“„ Download Resume</button>
        </>
      )}

      {/* Chatbot Icon */}
      <div style={{ position: "fixed", bottom: 20, right: 20 }}>
        {!chatOpen ? (
          <button onClick={() => setChatOpen(true)} style={{ borderRadius: "50%", padding: 15, background: "#2563eb", color: "#fff" }}>ðŸ’¬</button>
        ) : (
          <div style={{ width: 300, height: 400, background: "#fff", border: "1px solid #ccc", borderRadius: 10, display: "flex", flexDirection: "column" }}>
            <div style={{ background: "#2563eb", color: "#fff", padding: 10, display: "flex", justifyContent: "space-between" }}>
              <span>ðŸ¤– Chatbot</span><button onClick={() => setChatOpen(false)} style={{ background: "none", border: "none", color: "#fff" }}>âœ•</button>
            </div>
            <div style={{ flex: 1, overflowY: "auto", padding: 10 }}>
              {chat.map((m, i) => (
                <div key={i} style={{ textAlign: m.type === "user" ? "right" : "left", margin: "5px 0" }}>
                  <span style={{ background: m.type === "user" ? "#2563eb" : "#eee", color: m.type === "user" ? "#fff" : "#000", padding: "5px 10px", borderRadius: 10 }}>{m.msg}</span>
                </div>
              ))}
            </div>
            <div style={{ display: "flex", padding: 5 }}>
              <input value={input} onChange={e => setInput(e.target.value)} onKeyDown={e => e.key === "Enter" && send()} style={{ flex: 1 }} />
              <button onClick={send}>Send</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
