import React, { useState, useEffect, useMemo } from "react";

const SECTIONS = {
  education: /\b(education|qualifications|degree|university|college)\b/i,
  skills: /\b(skills|technical skills|key skills|competencies)\b/i,
  experience: /\b(experience|work experience|employment|career)\b/i,
  projects: /\b(projects?|portfolio)\b/i,
  certifications: /\b(certifications?|certificates?|licensed?)\b/i,
  contact: /\b(contact|email|phone|linkedin|github|address)\b/i,
};

const RESUME_TEMPLATES = {
  modern: {
    name: "Modern Professional",
    font: "Georgia, serif",
    headerBg: "#2563eb",
    textColor: "#1f2937",
    accent: "#3b82f6",
  },
  minimal: {
    name: "Minimal Clean",
    font: "Arial, sans-serif",
    headerBg: "#374151",
    textColor: "#111827",
    accent: "#6b7280",
  },
  corporate: {
    name: "Corporate Elite",
    font: "Times New Roman, serif",
    headerBg: "#1f2937",
    textColor: "#111827",
    accent: "#4b5563",
  },
  creative: {
    name: "Creative Bold",
    font: "Helvetica, sans-serif",
    headerBg: "#7c3aed",
    textColor: "#1f2937",
    accent: "#8b5cf6",
  },
};

export default function ResumeAnalyzer() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [selectedTemplate, setSelectedTemplate] = useState("modern");
  const [showPreview, setShowPreview] = useState(false);

  useEffect(() => {
    if (!resumeText.trim() && !jobDescription.trim()) return;

    const found = {};
    const missing = [];
    for (const [key, regex] of Object.entries(SECTIONS)) {
      if (regex.test(resumeText)) found[key] = true;
      else missing.push(key);
    }

    // Keyword matching from Job Description
    const jdWords = jobDescription
      .toLowerCase()
      .split(/\W+/)
      .filter((w) => w.length > 3);
    const resumeWords = resumeText.toLowerCase();
    let matchedKeywords = jdWords.filter((word) =>
      resumeWords.includes(word)
    );

    const atsScore =
      Math.max(0, 100 - missing.length * 15) +
      Math.min(20, matchedKeywords.length * 2);

    setAnalysis({ found, missing, atsScore, matchedKeywords });
  }, [resumeText, jobDescription]);

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length)
      fb.push(`Missing sections: ${analysis.missing.join(", ")}`);
    if (analysis.atsScore < 50)
      fb.push("‚ùå Low ATS score. Add missing sections immediately.");
    else if (analysis.atsScore < 80)
      fb.push("‚ö†Ô∏è Good foundation, but can improve.");
    else fb.push("‚úÖ Excellent resume structure! ATS-friendly.");
    if (analysis.matchedKeywords.length)
      fb.push(
        `Matched job description keywords: ${analysis.matchedKeywords.join(
          ", "
        )}`
      );
    return fb;
  }, [analysis]);

  const handleFile = (file) => {
    if (!file) return;
    if (file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => setResumeText(e.target.result);
      reader.readAsText(file);
    } else {
      alert("Please upload a .txt file only");
    }
  };

  const downloadPDF = () => {
    if (!resumeText.trim()) return;
    setShowPreview(true);
    setTimeout(() => {
      window.print();
    }, 300);
  };

  return (
    <section>
      {/* Upload */}
      <div className="card">
        <h2 className="section-title">üìÑ Upload Resume</h2>
        <input
          type="file"
          accept=".txt"
          onChange={(e) => handleFile(e.target.files[0])}
        />
      </div>

      {/* Job Description */}
      <div className="card">
        <h2 className="section-title">üìù Job Description</h2>
        <textarea
          rows="5"
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
          placeholder="Paste job description here..."
        />
      </div>

      {/* Templates */}
      {resumeText && (
        <div className="card">
          <h2 className="section-title">üé® Choose Template</h2>
          <div className="template-grid">
            {Object.entries(RESUME_TEMPLATES).map(([key, t]) => (
              <div
                key={key}
                className={`template-card ${
                  selectedTemplate === key ? "selected" : ""
                }`}
                onClick={() => setSelectedTemplate(key)}
              >
                <div
                  className="template-preview"
                  style={{ background: t.headerBg }}
                />
                <h3>{t.name}</h3>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Results */}
      {analysis && (
        <div className="card">
          <h2 className="section-title">üìä Analysis</h2>
          <p className="ats-score">ATS Score: {analysis.atsScore}%</p>
          <ul>
            {feedback.map((f, i) => (
              <li key={i}>{f}</li>
            ))}
          </ul>
          <button className="primary-btn" onClick={downloadPDF}>
            üìÑ Download PDF
          </button>
        </div>
      )}

      {/* Preview */}
      {showPreview && (
        <div className="preview-overlay" onClick={() => setShowPreview(false)}>
          <div id="resume-preview" className="preview-card">
            <style>{`
              @media print {
                body * { visibility: hidden; }
                #resume-preview, #resume-preview * { visibility: visible; }
                #resume-preview { position: absolute; left: 0; top: 0; width: 100%; }
              }
            `}</style>
            <div
              style={{
                fontFamily: RESUME_TEMPLATES[selectedTemplate].font,
                color: RESUME_TEMPLATES[selectedTemplate].textColor,
              }}
              dangerouslySetInnerHTML={{
                __html: resumeText.replace(/\n/g, "<br/>"),
              }}
            />
          </div>
        </div>
      )}
    </section>
  );
}
