import React, { useState, useEffect } from "react";

const SECTION_PATTERNS = {
  education: /(education|academic background|qualifications)/i,
  skills: /(skills|technical skills|key skills)/i,
  experience: /(experience|work history|employment)/i,
  projects: /(projects|personal projects|portfolio)/i,
  certifications: /(certifications|licenses)/i,
  contact: /(contact|email|phone|linkedin|github)/i,
};

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(null);

  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
  }, [resumeText, jobDescription]);

  const handleFile = (file) => {
    if (file?.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => setResumeText(e.target.result);
      reader.readAsText(file);
    } else alert("Please upload a .txt file only");
  };

  const handleFileUpload = (e) => handleFile(e.target.files[0]);
  const handleDrop = (e) => {
    e.preventDefault();
    handleFile(e.dataTransfer.files[0]);
  };

  const analyzeResume = () => {
    const found = {};
    const missing = [];

    Object.entries(SECTION_PATTERNS).forEach(([key, regex]) =>
      regex.test(resumeText) ? (found[key] = true) : missing.push(key)
    );

    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = jobDescription.toLowerCase().split(/\W+/);
      const resumeWords = resumeText.toLowerCase().split(/\W+/);
      const matches = jdWords.filter((w) => resumeWords.includes(w));
      ats = Math.min(100, Math.round((matches.length / jdWords.length) * 100));
    }

    setAnalysis({ found, missing });
    setAtsScore(ats);
  };

  const getFeedback = () => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length)
      fb.push(
        `Your resume is missing: ${analysis.missing.join(", ")}. Add these to strengthen it.`
      );
    if (atsScore < 50 && atsScore !== null)
      fb.push(
        "Your ATS score is low. Try aligning your resume keywords with the job description."
      );
    if (atsScore >= 50 && atsScore < 80)
      fb.push("Decent match, but adding more relevant skills will boost your score.");
    if (atsScore >= 80)
      fb.push("Strong alignment with JD. Consider polishing formatting for readability.");
    return fb;
  };

  return (
    <div
      className="min-h-screen bg-gray-100 text-gray-900 flex flex-col items-center p-6"
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
    >
      <h1 className="text-4xl font-bold mb-6">üöÄ Dynamic Resume Analyzer</h1>

      {/* Upload Section */}
      <div className="w-full max-w-4xl mb-6">
        <label className="block mb-2 font-semibold">
          Upload or Drag & Drop Resume (.txt)
        </label>
        <input type="file" accept=".txt" onChange={handleFileUpload} className="mb-4" />
        <div className="border-2 border-dashed rounded-lg p-6 text-center text-gray-500">
          Drag & Drop resume file here
        </div>
      </div>

      {/* Job Description */}
      <div className="w-full max-w-4xl mb-6">
        <h2 className="text-xl font-semibold mb-2">Job Description</h2>
        <textarea
          className="w-full h-80 p-4 border rounded-lg shadow-sm resize-none overflow-y-scroll"
          placeholder="Paste the job description here..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
        />
      </div>

      {/* Results */}
      {analysis && (
        <div className="w-full max-w-4xl bg-white p-6 rounded-xl shadow-md space-y-6">
          <h2 className="text-2xl font-bold">Analysis Results</h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <h3 className="font-semibold mb-2">‚úÖ Found Sections</h3>
              <ul className="list-disc ml-6 text-green-700">
                {Object.keys(analysis.found).map((s) => (
                  <li key={s}>{s}</li>
                ))}
              </ul>
            </div>

            <div>
              <h3 className="font-semibold mb-2">‚ùå Missing Sections</h3>
              <ul className="list-disc ml-6 text-red-600">
                {analysis.missing.map((s) => (
                  <li key={s}>{s}</li>
                ))}
              </ul>
            </div>

            <div>
              <h3 className="font-semibold mb-2">üìä ATS Score</h3>
              {atsScore !== null ? (
                <p className="text-lg font-bold">{atsScore}% match</p>
              ) : (
                <p className="text-gray-500">Paste JD to calculate score</p>
              )}
            </div>
          </div>

          {/* Feedback */}
          <div>
            <h3 className="font-semibold mb-2">üí° Feedback</h3>
            <ul className="list-disc ml-6 text-blue-700">
              {getFeedback().map((f, i) => (
                <li key={i}>{f}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}
