import React, { useState, useEffect, useMemo, useRef } from "react";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

const SECTIONS = {
  education: /\b(education|degree|university|college)\b/i,
  skills: /\b(skills|technical skills|competencies)\b/i,
  experience: /\b(experience|work experience|career)\b/i,
  projects: /\b(projects?|portfolio)\b/i,
  certifications: /\b(certifications?|certificates?)\b/i,
  contact: /\b(contact|email|phone|linkedin|github)\b/i,
};

const TEMPLATES = {
  modern: {
    name: "Modern Professional",
    font: "Georgia, serif",
    headerBg: "#2563eb",
    textColor: "#1f2937",
  },
  minimal: {
    name: "Minimal Clean",
    font: "Arial, sans-serif",
    headerBg: "#374151",
    textColor: "#111827",
  },
  creative: {
    name: "Creative Bold",
    font: "Helvetica, sans-serif",
    headerBg: "#7c3aed",
    textColor: "#1f2937",
  },
};

export default function ResumeAnalyzer() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [selectedTemplate, setSelectedTemplate] = useState("modern");
  const resumeRef = useRef(null);

  useEffect(() => {
    if (!resumeText && !jobDescription) return;

    const found = {};
    const missing = [];
    for (const [key, regex] of Object.entries(SECTIONS)) {
      if (regex.test(resumeText)) found[key] = true;
      else missing.push(key);
    }

    const jdWords = jobDescription
      .toLowerCase()
      .split(/\W+/)
      .filter((w) => w.length > 3);
    const resumeWords = resumeText.toLowerCase();
    const matchedKeywords = jdWords.filter((w) => resumeWords.includes(w));

    const atsScore =
      Math.max(0, 100 - missing.length * 15) +
      Math.min(20, matchedKeywords.length * 2);

    setAnalysis({ found, missing, atsScore, matchedKeywords });
  }, [resumeText, jobDescription]);

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length) fb.push(`Missing sections: ${analysis.missing.join(", ")}`);
    if (analysis.atsScore < 50) fb.push("‚ùå Low ATS score. Add missing sections immediately.");
    else if (analysis.atsScore < 80) fb.push("‚ö†Ô∏è Good resume, but can improve further.");
    else fb.push("‚úÖ Excellent resume structure!");
    if (analysis.matchedKeywords.length)
      fb.push(`Matched job keywords: ${analysis.matchedKeywords.join(", ")}`);
    return fb;
  }, [analysis]);

  const handleFile = (file) => {
    if (!file) return;
    if (file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => setResumeText(e.target.result);
      reader.readAsText(file);
    } else {
      alert("Upload a .txt file only");
    }
  };

  const downloadPDF = async () => {
    if (!resumeRef.current) return;
    const canvas = await html2canvas(resumeRef.current);
    const imgData = canvas.toDataURL("image/png");
    const pdf = new jsPDF("p", "pt", "a4");
    const width = pdf.internal.pageSize.getWidth();
    const height = (canvas.height * width) / canvas.width;
    pdf.addImage(imgData, "PNG", 0, 0, width, height);
    pdf.save("resume.pdf");
  };

  return (
    <section>
      {/* Upload */}
      <div className="card">
        <h2 className="section-title">üìÑ Upload Resume</h2>
        <input type="file" accept=".txt" onChange={(e) => handleFile(e.target.files[0])} />
      </div>

      {/* Job Description */}
      <div className="card">
        <h2 className="section-title">üìù Job Description</h2>
        <textarea
          rows="4"
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
          placeholder="Paste job description here..."
        />
      </div>

      {/* Templates */}
      {resumeText && (
        <div className="card">
          <h2 className="section-title">üé® Choose Template</h2>
          <div className="template-grid">
            {Object.entries(TEMPLATES).map(([key, t]) => (
              <div
                key={key}
                className={`template-card ${selectedTemplate === key ? "selected" : ""}`}
                onClick={() => setSelectedTemplate(key)}
              >
                <div className="template-preview" style={{ background: t.headerBg }} />
                <h3>{t.name}</h3>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Analysis */}
      {analysis && (
        <div className="card">
          <h2 className="section-title">üìä Analysis</h2>
          <p className="ats-score">ATS Score: {analysis.atsScore}%</p>
          <ul>{feedback.map((f, i) => <li key={i}>{f}</li>)}</ul>
          <button className="primary-btn" onClick={downloadPDF}>
            üìÑ Download PDF
          </button>
        </div>
      )}

      {/* Resume Preview (Styled with template) */}
      {resumeText && (
        <div
          ref={resumeRef}
          className="resume-preview"
          style={{
            fontFamily: TEMPLATES[selectedTemplate].font,
            color: TEMPLATES[selectedTemplate].textColor,
          }}
          dangerouslySetInnerHTML={{ __html: resumeText.replace(/\n/g, "<br/>") }}
        />
      )}
    </section>
  );
}
