import React, { useState, useEffect } from "react";

// Regex patterns for common resume sections
const SECTION_PATTERNS = {
  education: /(education|academic background|qualifications)/i,
  skills: /(skills|technical skills|key skills)/i,
  experience: /(experience|work history|employment)/i,
  projects: /(projects|personal projects|portfolio)/i,
  certifications: /(certifications|licenses)/i,
  contact: /(contact|email|phone|linkedin|github)/i,
};

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [theme, setTheme] = useState("light");
  const [atsScore, setAtsScore] = useState(null);

  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
  }, [resumeText, jobDescription]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (event) => setResumeText(event.target.result);
      reader.readAsText(file);
    } else {
      alert("Please upload a .txt file only");
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (event) => setResumeText(event.target.result);
      reader.readAsText(file);
    } else {
      alert("Please drop a .txt file only");
    }
  };

  const analyzeResume = () => {
    let foundSections = {};
    let missingSections = [];

    Object.entries(SECTION_PATTERNS).forEach(([section, regex]) => {
      if (regex.test(resumeText)) {
        foundSections[section] = true;
      } else {
        missingSections.push(section);
      }
    });

    // ATS score = keyword overlap % between JD and Resume
    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = jobDescription.toLowerCase().split(/\W+/);
      const resumeWords = resumeText.toLowerCase().split(/\W+/);
      const matches = jdWords.filter((word) => resumeWords.includes(word));
      ats = Math.min(100, Math.round((matches.length / jdWords.length) * 100));
    }

    setAnalysis({ foundSections, missingSections });
    setAtsScore(ats);
  };

  return (
    <div
      className={`min-h-screen flex flex-col items-center p-6 transition-colors duration-300 ${
        theme === "dark" ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900"
      }`}
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
    >
      <div className="flex justify-between w-full max-w-6xl mb-6">
        <h1 className="text-3xl font-bold">Dynamic Resume Analyzer</h1>
        <select
          className="p-2 border rounded-md"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
        >
          <option value="light">Light Theme</option>
          <option value="dark">Dark Theme</option>
          <option value="minimal">Minimal Theme</option>
        </select>
      </div>

      <div className="w-full max-w-6xl mb-4">
        <label className="block mb-2 font-semibold">Upload or Drag & Drop Resume (.txt)</label>
        <input type="file" accept=".txt" onChange={handleFileUpload} className="mb-4" />
        <div className="border-2 border-dashed rounded-md p-6 text-center text-gray-500">
          Drag & Drop resume file here
        </div>
      </div>

      <div
        className={`grid grid-cols-1 md:grid-cols-2 gap-6 w-full max-w-6xl ${
          theme === "minimal" ? "bg-white" : "bg-white shadow-lg"
        } p-6 rounded-xl`}
      >
        {/* Resume Input */}
        <div>
          <h2 className="text-xl font-semibold mb-2">Resume Text</h2>
          <textarea
            className="w-full h-80 p-3 border rounded-md"
            placeholder="Paste your resume text here..."
            value={resumeText}
            onChange={(e) => setResumeText(e.target.value)}
          />
        </div>

        {/* Job Description */}
        <div>
          <h2 className="text-xl font-semibold mb-2">Job Description</h2>
          <textarea
            className="w-full h-80 p-3 border rounded-md"
            placeholder="Paste the job description here..."
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
          />
        </div>
      </div>

      {analysis && (
        <div className="w-full max-w-6xl mt-6 bg-white p-6 rounded-xl shadow-md">
          <h2 className="text-2xl font-bold mb-4">Analysis Results</h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <h3 className="font-semibold mb-2">‚úÖ Found Sections</h3>
              <ul className="list-disc ml-6 text-green-700">
                {Object.keys(analysis.foundSections).map((section) => (
                  <li key={section}>{section}</li>
                ))}
              </ul>
            </div>

            <div>
              <h3 className="font-semibold mb-2">‚ùå Missing Sections</h3>
              <ul className="list-disc ml-6 text-red-600">
                {analysis.missingSections.map((section) => (
                  <li key={section}>{section}</li>
                ))}
              </ul>
            </div>

            <div>
              <h3 className="font-semibold mb-2">üìä ATS Score</h3>
              {atsScore !== null ? (
                <p className="text-lg font-bold">
                  {atsScore}% match with job description
                </p>
              ) : (
                <p className="text-gray-500">Provide JD to calculate score</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
