import React, { useState, useEffect, useMemo } from "react";

const SECTION = {
  education: /(education|qualifications)/i,
  skills: /(skills|technical skills|key skills)/i,
  experience: /(experience|work experience)/i,
  projects: /(projects)/i,
  certifications: /(certifications)/i,
  contact: /(contact|email|phone|linkedin|github)/i,
};

export default function App() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [atsScore, setAtsScore] = useState(null);
  const [dragActive, setDragActive] = useState(false);

  useEffect(() => {
    if (resumeText.trim()) analyzeResume();
  }, [resumeText, jobDescription]);

  const handleFile = (file) => {
    if (!file || file.type !== "text/plain") {
      return alert("Only text format supported (.txt)");
    }
    const reader = new FileReader();
    reader.onload = (e) => setResumeText(e.target.result);
    reader.readAsText(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    handleFile(e.dataTransfer.files[0]);
  };

  const splitWords = (text) =>
    text.toLowerCase().split(/\W+/).filter(Boolean);

  const analyzeResume = () => {
    const found = {};
    const missing = [];
    for (const [key, regex] of Object.entries(SECTION)) {
      regex.test(resumeText) ? (found[key] = true) : missing.push(key);
    }

    let ats = null;
    if (jobDescription.trim()) {
      const jdWords = splitWords(jobDescription);
      const resumeWords = splitWords(resumeText);
      const matches = jdWords.filter((w) => resumeWords.includes(w));
      ats = Math.min(
        100,
        Math.round((matches.length / jdWords.length) * 100)
      );
    }
    setAnalysis({ found, missing });
    setAtsScore(ats);
  };

  const feedback = useMemo(() => {
    if (!analysis) return [];
    const fb = [];
    if (analysis.missing.length) {
      fb.push(`Missing sections: ${analysis.missing.join(", ")}.`);
    }
    if (atsScore !== null) {
      if (atsScore < 50) fb.push("‚ö†Ô∏è Low score. Add more skills & keywords.");
      else if (atsScore < 80) fb.push("üü° Decent, but add more relevant keywords.");
      else fb.push("‚úÖ Excellent match! Keep it up.");
    }
    return fb;
  }, [analysis, atsScore]);

  return (
    <div
      className="min-h-screen bg-gradient-to-br from-gray-100 via-white to-gray-200 text-gray-900 flex flex-col items-center p-6"
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
    >
      <h1 className="text-5xl font-extrabold mb-8 text-blue-700 drop-shadow-md">
        Dynamic Resume Analyzer
      </h1>

      {/* Upload Section */}
      <div className="w-full max-w-4xl mb-6">
        <label className="block mb-2 font-semibold text-lg">
          Upload Resume (.txt)
        </label>
        <input
          type="file"
          accept=".txt"
          onChange={(e) => handleFile(e.target.files[0])}
          className="mb-4"
        />

        {/* Drag & Drop */}
        <div
          className={`border-2 border-dashed rounded-xl p-8 text-center cursor-pointer transition-all duration-300 ${
            dragActive
              ? "border-blue-500 bg-blue-50 text-blue-700 scale-105"
              : "border-gray-400 text-gray-500 hover:border-blue-400 hover:text-blue-600"
          }`}
          onDragEnter={() => setDragActive(true)}
          onDragLeave={() => setDragActive(false)}
          onDrop={handleDrop}
        >
          {dragActive
            ? "üöÄ Drop your resume here"
            : "üìÇ Drag & Drop your resume here"}
        </div>
      </div>

      {/* Job Description */}
      <div className="w-full max-w-4xl mb-6">
        <h2 className="text-2xl font-semibold mb-2">Job Description</h2>
        <textarea
          className="w-full min-h-[200px] max-h-[400px] p-4 border rounded-xl shadow-sm resize-y overflow-y-auto focus:ring-2 focus:ring-blue-500"
          placeholder="Paste the job description here..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
        />
      </div>

      {/* Results */}
      {analysis && (
        <div className="w-full max-w-4xl bg-white p-6 rounded-2xl shadow-lg space-y-6 animate-fadeIn">
          <h2 className="text-3xl font-bold text-gray-800">
            üìä Analysis Results
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {/* Found */}
            <div className="p-4 rounded-xl border bg-green-50">
              <h3 className="font-semibold mb-2 text-green-700">
                ‚úÖ Found Sections
              </h3>
              <ul className="list-disc ml-6 text-green-700">
                {Object.keys(analysis.found).map((s) => (
                  <li key={s}>{s}</li>
                ))}
              </ul>
            </div>

            {/* Missing */}
            <div className="p-4 rounded-xl border bg-red-50">
              <h3 className="font-semibold mb-2 text-red-700">
                ‚ùå Missing Sections
              </h3>
              <ul className="list-disc ml-6 text-red-600">
                {analysis.missing.map((s) => (
                  <li key={s}>{s}</li>
                ))}
              </ul>
            </div>

            {/* ATS Score */}
            <div className="p-4 rounded-xl border bg-blue-50 flex flex-col justify-between">
              <h3 className="font-semibold mb-2 text-blue-700">üéØ ATS Score</h3>
              {atsScore !== null ? (
                <>
                  <p className="text-lg font-bold mb-2">
                    {atsScore}% match
                  </p>
                  <div className="w-full bg-gray-200 rounded-full h-3">
                    <div
                      className={`h-3 rounded-full transition-all duration-700 ${
                        atsScore < 50
                          ? "bg-red-500"
                          : atsScore < 80
                          ? "bg-yellow-500"
                          : "bg-green-500"
                      }`}
                      style={{ width: `${atsScore}%` }}
                    ></div>
                  </div>
                </>
              ) : (
                <p className="text-gray-500">Paste JD to calculate score</p>
              )}
            </div>
          </div>

          {/* Feedback */}
          <div className="p-4 rounded-xl border bg-gray-50">
            <h3 className="font-semibold mb-2 text-gray-800">üí° Feedback</h3>
            <ul className="list-disc ml-6 text-gray-700">
              {feedback.map((f, i) => (
                <li key={i}>{f}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}
