import React, { useEffect, useMemo, useRef, useState } from "react";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

/* ---------- config ---------- */
const SECTIONS = {
  contact: /\b(contact|email|phone|linkedin|github|address)\b/i,
  summary: /\b(summary|objective|profile)\b/i,
  experience: /\b(experience|work experience|employment|career)\b/i,
  skills: /\b(skills|technical skills|competencies)\b/i,
  education: /\b(education|degree|university|college)\b/i,
  projects: /\b(projects?|portfolio)\b/i,
  certifications: /\b(certifications?|certificates?|license|licensed?)\b/i,
};

const TEMPLATES = {
  modern: {
    name: "Modern Professional",
    className: "theme-modern",
    fontStack: "ui-sans-serif, system-ui, -apple-system, Segoe UI, Arial",
  },
  minimal: {
    name: "Minimal Clean",
    className: "theme-minimal",
    fontStack: "Arial, Helvetica, sans-serif",
  },
  creative: {
    name: "Creative Bold",
    className: "theme-creative",
    fontStack: "Helvetica, Arial, sans-serif",
  },
};

/* ---------- helpers ---------- */
const STOPWORDS = new Set([
  "and","the","with","from","that","this","your","their","our","for","into","able",
  "will","shall","must","have","has","had","are","was","were","you","they","them",
  "over","under","about","above","below","not","only","but","also","more","than",
  "such","etc","using","use","used","strong","good","great","work","role","team",
  "skills","requirements","responsibilities","job","description","looking","plus",
  "preferred","required","experience","years","year","developer","engineer","data"
]);

function extractKeywords(text) {
  return [...new Set(
    text
      .toLowerCase()
      .split(/[^a-z0-9+#.]/i)
      .map(w => w.trim())
      .filter(w => w.length > 2 && !STOPWORDS.has(w))
  )];
}

function firstNonEmptyLine(s) {
  return (s || "")
    .split(/\r?\n/)
    .map(x => x.trim())
    .find(x => x.length > 0) || "Candidate Name";
}

function plainToHTML(resumeText) {
  // Upgrade common section headings to <h2>, keep other lines as <p>
  const lines = (resumeText || "").split(/\r?\n/);
  const out = [];
  for (let raw of lines) {
    const line = raw.trim();
    if (!line) continue;
    const isHeading = Object.values(SECTIONS).some(r => r.test(line));
    if (isHeading) out.push(`<h2>${escapeHTML(line)}</h2>`);
    else out.push(`<p>${escapeHTML(line)}</p>`);
  }
  return out.join("\n");
}
function escapeHTML(s) {
  return s
    .replaceAll("&","&amp;")
    .replaceAll("<","&lt;")
    .replaceAll(">","&gt;");
}

/* ---------- component ---------- */
export default function ResumeAnalyzer() {
  const [resumeText, setResumeText] = useState("");
  const [jobDescription, setJobDescription] = useState("");
  const [selectedTemplate, setSelectedTemplate] = useState("modern");
  const [analysis, setAnalysis] = useState(null);

  const previewRef = useRef(null);   // on-screen preview + PDF source

  // compute analysis
  useEffect(() => {
    if (!resumeText && !jobDescription) { setAnalysis(null); return; }

    // structure: found/missing sections
    const found = {};
    const missing = [];
    for (const [k, rgx] of Object.entries(SECTIONS)) {
      if (rgx.test(resumeText)) found[k] = true;
      else missing.push(k);
    }

    // JD keyword coverage
    const jdKeys = extractKeywords(jobDescription);
    const resumeLower = resumeText.toLowerCase();
    const matched = jdKeys.filter(k => resumeLower.includes(k));
    const missingKeys = jdKeys.filter(k => !resumeLower.includes(k)).slice(0, 20);

    // ATS scoring: structure (0..60) + keyword coverage (0..40)
    const structureScore = Math.max(0, 60 - missing.length * 10);
    const keywordCoverage = jdKeys.length ? (matched.length / jdKeys.length) : 0;
    const keywordScore = Math.round(40 * keywordCoverage);
    const atsScore = Math.max(0, Math.min(100, structureScore + keywordScore));

    setAnalysis({
      found, missing, matched, missingKeys, atsScore,
      uniqueJD: jdKeys.length
    });
  }, [resumeText, jobDescription]);

  // user feedback
  const feedback = useMemo(() => {
    if (!analysis) return [];
    const list = [];
    if (analysis.missing.length) list.push(`Missing sections: ${analysis.missing.join(", ")}`);
    list.push(
      analysis.atsScore >= 80 ? "‚úÖ Strong ATS alignment."
      : analysis.atsScore >= 60 ? "‚ö†Ô∏è Decent, add more JD keywords."
      : "‚ùå Weak ATS alignment. Fill core sections and include relevant JD terms."
    );
    if (analysis.missingKeys?.length)
      list.push(`Top missing JD keywords: ${analysis.missingKeys.slice(0,10).join(", ")}`);
    return list;
  }, [analysis]);

  /* ------- handlers ------- */
  const handleFile = (file) => {
    if (!file) return;
    if (file.type !== "text/plain") { alert("Upload a .txt file only"); return; }
    const reader = new FileReader();
    reader.onload = e => setResumeText(String(e.target.result || ""));
    reader.readAsText(file);
  };

  const downloadPDF = async () => {
    // capture the single preview node; add a subtle shadow blocker to avoid double rendering
    const node = previewRef.current;
    if (!node) return;

    // Scroll to top to avoid clipped capture on small screens
    window.scrollTo({ top: 0, behavior: "instant" });

    const canvas = await html2canvas(node, {
      scale: 2,
      backgroundColor: "#ffffff",
      useCORS: true
    });

    const img = canvas.toDataURL("image/png");
    const pdf = new jsPDF("p", "pt", "a4");
    const pageW = pdf.internal.pageSize.getWidth();
    const pageH = pdf.internal.pageSize.getHeight();

    const imgW = pageW; // full width
    const imgH = (canvas.height * imgW) / canvas.width;

    // If longer than one page, add pages cleanly (still one copy)
    let y = 0;
    let remaining = imgH;
    while (remaining > 0) {
      pdf.addImage(img, "PNG", 0, -y, imgW, imgH);
      remaining -= pageH;
      y += pageH;
      if (remaining > 0) pdf.addPage();
    }
    pdf.save("resume.pdf");
  };

  const candidateName = useMemo(() => firstNonEmptyLine(resumeText), [resumeText]);
  const htmlBody = useMemo(() => plainToHTML(resumeText), [resumeText]);

  return (
    <section>
      {/* INPUTS */}
      <div className="grid-2">
        <div className="card">
          <h2 className="section-title">üìÑ Resume (paste or upload)</h2>
          <div className="row gap-sm">
            <input
              type="file"
              accept=".txt"
              onChange={(e) => handleFile(e.target.files?.[0])}
            />
          </div>
          <textarea
            className="text-input resume-input"
            rows={14}
            placeholder="Paste your resume text here..."
            value={resumeText}
            onChange={(e) => setResumeText(e.target.value)}
          />
        </div>

        <div className="card">
          <h2 className="section-title center">üìù Job Description</h2>
          <textarea
            className="text-input jd-input center-text"
            placeholder="Paste JD here‚Ä¶"
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
          />
          <p className="hint center">Fixed size ‚Ä¢ scrolls ‚Ä¢ centered text (per your request)</p>
        </div>
      </div>

      {/* TEMPLATES */}
      {resumeText && (
        <div className="card">
          <h2 className="section-title">üé® Choose Template</h2>
          <div className="template-grid">
            {Object.entries(TEMPLATES).map(([key, t]) => (
              <button
                key={key}
                type="button"
                className={`template-card ${selectedTemplate === key ? "selected" : ""}`}
                onClick={() => setSelectedTemplate(key)}
                title={t.name}
              >
                <div className={`template-swatch ${t.className}`} />
                <div className="template-name">{t.name}</div>
              </button>
            ))}
          </div>
        </div>
      )}

      {/* ANALYSIS */}
      {analysis && (
        <div className="card">
          <h2 className="section-title">üìä ATS Analysis</h2>

          <div className="score-row">
            <div
              className="score-ring"
              style={{ "--pct": `${analysis.atsScore}%` }}
            >
              <div className="score-num">{analysis.atsScore}%</div>
              <div className="score-label">ATS score</div>
            </div>

            <div className="analysis-points">
              <ul>
                {feedback.map((f, i) => <li key={i}>{f}</li>)}
              </ul>
              <div className="chips">
                {analysis.matched?.slice(0, 20).map((k) => (
                  <span key={k} className="chip ok">{k}</span>
                ))}
                {analysis.missingKeys?.slice(0, 10).map((k) => (
                  <span key={k} className="chip warn">{k}</span>
                ))}
              </div>
            </div>
          </div>

          <div className="actions">
            <button className="btn primary" onClick={downloadPDF}>üìÑ Download PDF</button>
          </div>
        </div>
      )}

      {/* PREVIEW (this exact node is exported to PDF) */}
      {resumeText && (
        <div
          ref={previewRef}
          className={`resume-sheet ${TEMPLATES[selectedTemplate].className}`}
          style={{ fontFamily: TEMPLATES[selectedTemplate].fontStack }}
        >
          <div className="sheet-header">
            <div className="avatar">{candidateName.slice(0,1).toUpperCase()}</div>
            <div className="headings">
              <h1 className="name">{candidateName}</h1>
              <div className="tagline">Professional Resume</div>
            </div>
          </div>
          <div
            className="sheet-body"
            dangerouslySetInnerHTML={{ __html: htmlBody }}
          />
        </div>
      )}
    </section>
  );
}
